INSERT INTO replik VALUES(1692,6,NULL,'Doctor',' Wow. He must''ve been a total Zoidberg.');
INSERT INTO replik VALUES(1693,6,NULL,'Hermes',' It was Zoidberg! He brutalised us with power tools, gypsy curses, and hypnosis!');
INSERT INTO replik VALUES(1694,6,NULL,'Amy',' Yes, master.');
INSERT INTO replik VALUES(1695,6,NULL,'Doctor',' You''ll all get through this. I give you my word as a real doctor.');
INSERT INTO replik VALUES(1696,6,NULL,'Bender',' What about me?');
INSERT INTO replik VALUES(1697,6,NULL,'Doctor',' What did ''e do to you?');
