INSERT INTO replik VALUES(1724,6,NULL,'Zoidberg',' Just because you were in the swamp, doesn''t necessarily meanâ');
INSERT INTO replik VALUES(1725,6,NULL,'Farnsworth',' I was thirsty from the flight! I drank swamp water! Look, Zoidberg. You''re a loyal friend.');
INSERT INTO replik VALUES(1726,6,NULL,'Zoidberg',' Friend?!');
INSERT INTO replik VALUES(1727,6,NULL,'Farnsworth',' But I''m doomed. I might as well capture a Yeti and die trying.');
