INSERT INTO replik VALUES(1747,6,NULL,'Farnsworth',' Oh, fuff.');
INSERT INTO replik VALUES(1748,6,NULL,'Zoidberg',' Yes, fuff. How can I ever repay you?! Do you need a slave? Or maybe a butler?');
