INSERT INTO replik VALUES(3802,1,NULL,'Leela',' This is hopeless. If we''re gonna get blown to bits, we might as well do it in the comfort of our own home.');
INSERT INTO replik VALUES(3803,1,NULL,'Farnsworth',' You''ll be the captain, you''ll be the delivery boy, and you''ll be the alcoholic, foul-mouthed-- Oh, God, you''re alive. I mean, thank God you''re alive! Sorry, check back in three days, a week at the most.');
