INSERT INTO replik VALUES(3829,1,NULL,'Fry',' It''s crazy! How could they even know about a show from a thousand years ago?');
INSERT INTO replik VALUES(3830,1,NULL,'Farnsworth',' Well, Omicron Persei 8 is about a thousand light years away. So the electro-magnetic waves would just recently have gotten there. You see--');
INSERT INTO replik VALUES(3831,1,NULL,'Fry',' Magic. Got it.');
