INSERT INTO replik VALUES(3869,1,NULL,'Bender',' We''ll be back after this word from Crazy Bender''s Discount Stereo.');
INSERT INTO replik VALUES(3870,1,NULL,'Fry',' Married? Jenny can''t get married.');
