INSERT INTO replik VALUES(4864,2,NULL,'Bender',' Hey, buddy, heard you needed cheering up! Well, old Bender''ll make you laugh. Look at me, look! Oh, man, I gotta work on my act!');
INSERT INTO replik VALUES(4865,2,NULL,'Fry',' I feel like a rat. Here I am whining like a pig while all along Leela was lonely as a frog. I could kick myself.');
