INSERT INTO replik VALUES(4871,2,NULL,'Bender',' Xmas Eve; another pointless day where I accomplish nothing.');
INSERT INTO replik VALUES(4872,2,NULL,'Linda',' The holiday season is a time of celebration for most. But it is also a time to remember the tragic suffering of the less fortunate.');
