INSERT INTO replik VALUES(5049,2,NULL,'Farnsworth',' Mm-hm... Oh-ho! Aha! There''s the problem.');
INSERT INTO replik VALUES(5050,2,NULL,'Zoidberg',' Give it to me straight, Professor. Is it fin rot? It''s fin rot, isn''t it? Tell me it''s not fin rot!');
INSERT INTO replik VALUES(5051,2,NULL,'Farnsworth',' Relax, my chiton-y chum. There''s no problem. You''re just heavy with male jelly.');
INSERT INTO replik VALUES(5052,2,NULL,'Leela',' Bleck!');
INSERT INTO replik VALUES(5053,2,NULL,'Farnsworth',' It must be mating season for Zoidberg''s people; a chaotic time when his behaviour is dictated by the tiny brain in his rump.');
INSERT INTO replik VALUES(5054,2,NULL,'Fry',' Eck!');
INSERT INTO replik VALUES(5055,2,NULL,'Farnsworth',' There''s only one thing we can do--');
INSERT INTO replik VALUES(5056,2,NULL,'Bender',' I''ll get the water boiling!');
INSERT INTO replik VALUES(5057,2,NULL,'Farnsworth',' We, by which I mean you, will have to rush him to his ancient homeworld which will shortly erupt in an orgy of invertebrate sex.');
INSERT INTO replik VALUES(5058,2,NULL,'Fry',' Oh, baby, I''m there!');
INSERT INTO replik VALUES(5059,2,NULL,'Leela',' Fry, do you even understand the word "invertebrate"?');
INSERT INTO replik VALUES(5060,2,NULL,'Fry',' Nope, but that''s not the word I''m interested in. Uh, no need to pack pants, people. Let''s roll!');
INSERT INTO replik VALUES(5061,2,NULL,'Amy',' Dr. Zoidberg said I should hold these while he''s gone.');
INSERT INTO replik VALUES(5062,2,NULL,'Bender',' Moron!');
INSERT INTO replik VALUES(5063,2,NULL,'Decapodian Man #1',' Welcome home, old friend. Just 19 hours until the mating frenzy!');
