INSERT INTO replik VALUES(5212,2,NULL,'Leela',' Ew!');
INSERT INTO replik VALUES(5213,2,NULL,'Bender',' Oh, my God!');
INSERT INTO replik VALUES(5214,2,NULL,'Zoidberg',' The frenzy! Oh, the greatest experience in life and I missed it.');
INSERT INTO replik VALUES(5215,2,NULL,'Fry',' Aw. I''m sorry, Doc.');
