INSERT INTO replik VALUES(5254,2,NULL,'Announcer #1',' Cop Department will return, after these messages.');
INSERT INTO replik VALUES(5255,2,NULL,'Announcer #2',' Is today''s hectic lifestyle making you tense and impatient?');
