INSERT INTO replik VALUES(5263,2,NULL,'Leela',' Sounds like your kinda place, Fry. Wanna go?');
INSERT INTO replik VALUES(5264,2,NULL,'Fry',' Nah. If I ever wanna go back to the year 2000 I''ll just freeze myself again.');
INSERT INTO replik VALUES(5265,2,NULL,'Bender',' C''mon, Fry, I really wanna see it. You know how I yearn for a simpler time. A time of barn dances and buggy rides, before life was cheapened by heartless hi-tech machines.');
INSERT INTO replik VALUES(5266,2,NULL,'Leela',' But, Bender, you are--');
INSERT INTO replik VALUES(5267,2,NULL,'Bender',' Blah, blah, blah, blah, blah, blah, blah, blah, blah, blah, blah, blah...');
