INSERT INTO replik VALUES(5994,2,NULL,'Farnsworth',' Yo!');
INSERT INTO replik VALUES(5995,2,NULL,'Bender',' My man!');
INSERT INTO replik VALUES(5996,2,NULL,'Amy',' So how was the Spleef Nebula?');
INSERT INTO replik VALUES(5997,2,NULL,'Hermes',' The flight had a stopover on the Brain Slug Planet. Hermes liked it so much he decided to stay of his own free will.');
INSERT INTO replik VALUES(5998,2,NULL,'Fry',' Hermes has all the fun. Wait a second! He''s got a Brain Slug on his head!');
INSERT INTO replik VALUES(5999,2,NULL,'Leela',' Shh! You''re gonna get us all assimilated!');
