INSERT INTO replik VALUES(8841,2,NULL,'Bender',' You were only frozen for two days. Uh, by the way, I broke your bed.');
INSERT INTO replik VALUES(8842,2,NULL,'Farnsworth',' You were in Pauly Shore''s tube and they were delivering it to his movie screening.');
INSERT INTO replik VALUES(8843,2,NULL,'Leela',' But, when they noticed you weren''t him, they chucked you in a ditch.');
INSERT INTO replik VALUES(8844,2,NULL,'Fry',' So you''re saying these aren''t the decaying ruins of New York in the year 4000?');
INSERT INTO replik VALUES(8845,2,NULL,'Farnsworth',' You wish! You''re in Los Angeles!');
INSERT INTO replik VALUES(8846,2,NULL,'Fry',' But there was this gang of 10-year-olds with guns.');
