INSERT INTO replik VALUES(8850,2,NULL,'Fry',' But the air is green and there''s no sign of civilisation whatsoever.');
INSERT INTO replik VALUES(8851,2,NULL,'Bender',' He just won''t stop with the social commentary.');
INSERT INTO replik VALUES(8852,2,NULL,'Fry',' And the people are all phoneys. No one reads. Everything has cilantro on it-- Michelle, baby!');
