INSERT INTO replik VALUES(8861,3,NULL,'Fry',' He''s got me beat!');
INSERT INTO replik VALUES(8862,3,NULL,'Hermes',' Now we all love Nibbler, so it''s only fair that we all pitch in and clean it up together. Still, I propose we make Zoidberg do it. All in favour?');
INSERT INTO replik VALUES(8863,3,NULL,'Leela',' Yeah!');
INSERT INTO replik VALUES(8864,3,NULL,'Bender',' Alright, yup.');
INSERT INTO replik VALUES(8865,3,NULL,'Hermes',' All opposed? All abstaining?');
