INSERT INTO replik VALUES(8867,3,NULL,'Fry',' He''s dead.');
INSERT INTO replik VALUES(8868,3,NULL,'Bender',' What?');
INSERT INTO replik VALUES(8869,3,NULL,'Amy',' Oh, no!');
INSERT INTO replik VALUES(8870,3,NULL,'Farnsworth',' He always seemed so full of life; laughing, singing, begging for scraps. And now this.');
INSERT INTO replik VALUES(8871,3,NULL,'Zoidberg',' Why with all the crying? So that''s where I left my shell.');
