INSERT INTO replik VALUES(13665,3,NULL,'Fry',' Ms. Johnson, you''ve never lied to me. Am I still a good person?');
INSERT INTO replik VALUES(13666,3,NULL,'Suz',' I don''t know, sir. I''m a program built into the intercom.');
INSERT INTO replik VALUES(13667,3,NULL,'Fry',' I''ve got to redeem myself. Somehow, sometime, for some reason. I''ll block this takeover!');
