INSERT INTO replik VALUES(13699,3,NULL,'Horrible Gelatinous Stockbroker',' What are you solids griping about? Your shares are worth $107 apiece.');
INSERT INTO replik VALUES(13700,3,NULL,'Leela',' They are? Oh, my God! I''m a millionaire! Suddenly I have an opinion about that capital gains tax!');
