INSERT INTO replik VALUES(13709,3,NULL,'Fry',' Oh, my God! His boneitis!');
INSERT INTO replik VALUES(13710,3,NULL,'Steve Castle',' I was so busy being an 80''s guy, I forgot to cure it. My only regret is ... that I have ... boneitis!');
INSERT INTO replik VALUES(13711,3,NULL,'Fry',' He''s dead.');
