INSERT INTO replik VALUES(15175,4,NULL,'Macaulay Culkon',' --At Veracruz. So, if you''ll kindly hand me my poncho--');
INSERT INTO replik VALUES(15176,4,NULL,'Bender',' Boo! Bring on Bender! Boo! Bender''s the greatest!');
INSERT INTO replik VALUES(15177,4,NULL,'Calculon',' Sorry, kid. You''re flailing up there. 90 actors and they all got boo-ed. I just wish we could get this Bender I keep hearing about. They say he''s the greatest.');
