INSERT INTO replik VALUES(15179,4,NULL,'Calculon',' Send him in forthwith!');
INSERT INTO replik VALUES(15180,4,NULL,'Bender',' Boo! Not as good as Bender! Boo!');
INSERT INTO replik VALUES(15181,4,NULL,'Casting Director',' Mr. Bender?');
