INSERT INTO replik VALUES(15211,4,NULL,'Calculon',' He stole the scene and my money? That''s it! I demand you fire this felonious ham!');
INSERT INTO replik VALUES(15212,4,NULL,'Director',' The network Execubots are coming!');
