INSERT INTO replik VALUES(15214,4,NULL,'Betabot',' Presenting the president of the network.');
INSERT INTO replik VALUES(15215,4,NULL,'Network President',' Greetings, gentlemen. You already know my Execubots  Executive Alpha, programmed to like things it has seen before.');
