INSERT INTO replik VALUES(15264,4,NULL,'Cubert',' Bender loves to burgle!');
INSERT INTO replik VALUES(15265,4,NULL,'Dwight',' Hold up, though. Who could we rob? We don''t even know anyone with cool stuff.');
INSERT INTO replik VALUES(15266,4,NULL,'Cubert',' Duh! We know someone who runs on pure cool fuel  Bender!');
