INSERT INTO replik VALUES(15272,4,NULL,'Fry',' Hey, Bender. Hey, Bender. Hey, Bender. Hey, Bender.');
INSERT INTO replik VALUES(15273,4,NULL,'Farnsworth',' What''s going on in here? Oh. Now I''m really outraged.');
