INSERT INTO replik VALUES(15276,4,NULL,'Cubert',' Relax, Dad. We just invited a few friends over.');
INSERT INTO replik VALUES(15277,4,NULL,'Farnsworth',' Hmm. There''s something wrong with your story but I can''t put my finger on it. Of course! You don''t have friends!');
INSERT INTO replik VALUES(15278,4,NULL,'Hermes',' Yeah. Why do these popular kids consider you cool all of a sudden?');
INSERT INTO replik VALUES(15279,4,NULL,'Dwight',' Um ... we''re just cool, is all!');
INSERT INTO replik VALUES(15280,4,NULL,'Farnsworth',' Balderdash! I''ll be the judge of who''s cool, using the cool-o-meter!');
