INSERT INTO replik VALUES(15285,4,NULL,'Hermes',' There''s enough cool stuff here to furnish a happenin'' pad. Where did it all come from?');
INSERT INTO replik VALUES(15286,4,NULL,'Tinny Tim',' Don''t tell, comrades. All for one and one for all!');
