INSERT INTO replik VALUES(15302,4,NULL,'Calculon',' Great Shatner''s ghost!');
INSERT INTO replik VALUES(15303,4,NULL,'Bender',' We demand that all TV''s be equipped with a B-Chip that blocks Bender from appearing on the screen.');
INSERT INTO replik VALUES(15304,4,NULL,'Farnsworth',' Booya!');
INSERT INTO replik VALUES(15305,4,NULL,'Network President',' I''m afraid the answer is a gritty, in-your-face no.');
INSERT INTO replik VALUES(15306,4,NULL,'Bender',' Then I quit.');
