INSERT INTO replik VALUES(15309,4,NULL,'Hermes',' What makes you think that''ll work?');
INSERT INTO replik VALUES(15310,4,NULL,'Farnsworth',' I saw it on TV in that episode where Bender shot Calculon. How cool was that?');
INSERT INTO replik VALUES(15311,4,NULL,'Bender',' Ooh, yeah! C''mon--');
INSERT INTO replik VALUES(15312,4,NULL,'Farnsworth',' Quit the show!');
INSERT INTO replik VALUES(15313,4,NULL,'Network President',' Do the scene.');
INSERT INTO replik VALUES(15314,4,NULL,'Farnsworth',' I''m a cold-blooded punk!');
INSERT INTO replik VALUES(15315,4,NULL,'Network President',' I once put a laugh track on a sitcom that had no jokes in it.');
INSERT INTO replik VALUES(15316,4,NULL,'Bender',' Hey, look! The prop guy has a lamp!');
