INSERT INTO replik VALUES(19644,5,NULL,'Yivo',' Please, please, give me another chance. We rushed into this relationship, but let''s start over as friends and see where things go.');
INSERT INTO replik VALUES(19645,5,NULL,'Amy',' Take me back, Kif?');
INSERT INTO replik VALUES(19646,5,NULL,'Kif',' I don''t think I can. How could you? My body wasn''t even warm yet.');
