INSERT INTO replik VALUES(19648,5,NULL,'Kif',' For about five minutes.');
INSERT INTO replik VALUES(19649,5,NULL,'Zapp (Sticking his head into the conversation)',' That''s all it takes.');
INSERT INTO replik VALUES(19650,5,NULL,'Hedonism Bot',' Whereas Calculon has sullied Bender''s reputation by insinuating that he is a human-lover, a duel is hereby engaged. Bender, as the offended party, shall have choice of weapon.');
INSERT INTO replik VALUES(19651,5,NULL,'Bender',' Planetary annihilators.');
