INSERT INTO replik VALUES(19655,5,NULL,'Bender',' One, two, three...');
INSERT INTO replik VALUES(19656,5,NULL,'Hedonism Bot',' Oh, how dreadfully exciting. Oh, yes.');
INSERT INTO replik VALUES(19657,5,NULL,'Zapp',' At 0800 hours, we received the following transmission from Yivo.');
INSERT INTO replik VALUES(19658,5,NULL,'Yivo',' Hey, it''s Yivo. Want to do something Friday? Call me.');
INSERT INTO replik VALUES(19659,5,NULL,'Nixon',' Analysis?');
INSERT INTO replik VALUES(19660,5,NULL,'Fry',' Mr. President, I think we need to seriously consider the possibility of going on this date.');
INSERT INTO replik VALUES(19661,5,NULL,'Miss Vega 4',' I agree. Yivo makes me feel sexy, and I''m asexual.');
INSERT INTO replik VALUES(19662,5,NULL,'Zapp',' Very well, but no sugar on the first date.');
INSERT INTO replik VALUES(19663,5,NULL,'Nixon',' All in favour? All opposed?  Motion is carried.');
