INSERT INTO replik VALUES(19665,5,NULL,'Bender',' ...six, seven, eight!');
INSERT INTO replik VALUES(19666,5,NULL,'Bender',' Nine, ten, fire. Yes, I got him! Wohoo!');
INSERT INTO replik VALUES(19667,5,NULL,'Nixon',' My fellow Earthlicans, commence preparations for our date with Yivo.');
INSERT INTO replik VALUES(19668,5,NULL,'ALL YIVOS',' I had a wonderful time.');
INSERT INTO replik VALUES(19669,5,NULL,'ALL NOT YIVO',' Me, too!');
INSERT INTO replik VALUES(19670,5,NULL,'Calculon',' Bender, you''ve cheated, insulted and maimed me.');
INSERT INTO replik VALUES(19671,5,NULL,'Bender',' Uh-huh.');
INSERT INTO replik VALUES(19672,5,NULL,'Calculon',' And thoroughly destroyed our own secret headquarters in the process.');
INSERT INTO replik VALUES(19673,5,NULL,'Bender',' Hey, it''s easy to criticize.');
INSERT INTO replik VALUES(19674,5,NULL,'Calculon',' I''m so disgusted by your loathsome behaviour that I hereby resign from this imbecilic club and relinquish the presidency to you.');
