INSERT INTO replik VALUES(19787,5,NULL,'Fry',' Oh, great. So what am I supposed to do now?');
INSERT INTO replik VALUES(19788,5,NULL,'Yivo',' Go home. Find a girl from your own universe and live on top of her.');
