INSERT INTO replik VALUES(19904,5,NULL,'Bender',' Uh, um... I am Titanius Anglesmith, fancy man of Cornwood.');
INSERT INTO replik VALUES(19905,5,NULL,'Farnsworth (Angrily)',' Everybody out of the conference room. I am calling a conference. (To the crew ) Everybody get in here. You wasted precious fuel just because you were insulted by some redneck yokel from beyond the stars?');
INSERT INTO replik VALUES(19906,5,NULL,'Leela',' It was only half a ball.');
INSERT INTO replik VALUES(19907,5,NULL,'Farnsworth',' That''s not the point. Your temper is out of control. And to think I''d have never even known if it weren''t for the lengthy and unsolicited tattling of Dr. Zoidberg.');
INSERT INTO replik VALUES(19908,5,NULL,'Zoidberg',' She also took home two rolls of Scotch tape.');
INSERT INTO replik VALUES(19909,5,NULL,'Farnsworth',' Thank you, Dr. Zoidberg. Hermes, incentivize that employee. As for you, Leela, I''m letting you off with a warning.');
