INSERT INTO replik VALUES(19926,5,NULL,'Narrator',' In the end, it was not guns or bombs that defeated the aliens, but that humblest of all God''s creatures, the Tyrannosaurus Rex.');
INSERT INTO replik VALUES(19927,5,NULL,'Leela',' I can''t believe TiVo suggested that piece of... Ow! Come on, Hermes. Surely you have the authority to remove this damn collar.');
