INSERT INTO replik VALUES(20639,5,NULL,'Hutch',' Seriously, do it. (Telepathically)  Testing, testing. Do you read me?');
INSERT INTO replik VALUES(20640,5,NULL,'Fry (Telepathically)',' Loud and clear.');
