INSERT INTO replik VALUES(20795,5,NULL,'Mr. Wong',' First of all, it 12% . Second, yes, you betcha.');
INSERT INTO replik VALUES(20796,5,NULL,'Leela',' But you have no idea what life forms might be evolving out there.');
INSERT INTO replik VALUES(20797,5,NULL,'Mr. Wong',' That''s exactly why I''m hiring an impartial scientist to perform an environmental survey. That''s him in the money shower.');
