INSERT INTO replik VALUES(20799,5,NULL,'Farnsworth',' Status reports, Science Officer.');
INSERT INTO replik VALUES(20800,5,NULL,'Bender',' Zilcho. No sign of life or intelligence. (Telepathically)  Just like Fry on a date.');
