INSERT INTO replik VALUES(20892,5,NULL,'ALL',' Welcome, bro.');
INSERT INTO replik VALUES(20893,5,NULL,'Hutch',' Sorry I bashed your head in, buddy, but I had to. You see, the fate of the universe depends on you.');
