INSERT INTO replik VALUES(21366,5,NULL,'Amy',' And dodo birds.');
INSERT INTO replik VALUES(21367,5,NULL,'Leela',' And white rhinos.');
INSERT INTO replik VALUES(21368,5,NULL,'Hermes',' And striped biologist-taunters.');
INSERT INTO replik VALUES(21369,5,NULL,'Striped biologist-taunter',' What are you gonna do, shoot us?');
INSERT INTO replik VALUES(21370,5,NULL,'Encyclopod',' Life! These once extinct plants and animals are my gift to the universe. Through untold generations, my race has treasured their DNA. Treat them wisely with the knowledge that all species are precious.');
INSERT INTO replik VALUES(21371,5,NULL,'Leela',' This is unbelievable. What''s going on?');
INSERT INTO replik VALUES(21372,5,NULL,'Hutch',' To answer that, I must tell you a story. A story of two alien species so ancient that-');
