INSERT INTO replik VALUES(21782,1,NULL,'Robot Devil',' Sorry, but I hold all the cards here. There''s nothing I can do. Now, if you''ll just sign this fiddle contest waiver.');
INSERT INTO replik VALUES(21783,1,NULL,'Leela',' Wait. What fiddle contest?');
