INSERT INTO replik VALUES(21825,6,NULL,'Zapp',' It does on CSI  Miami.');
INSERT INTO replik VALUES(21826,6,NULL,'Nixon',' They fought back with advance military hardware, but it was like shooting BB''s at Bebe Rebozo.');
