INSERT INTO replik VALUES(21836,6,NULL,'Nixon',' Alright, Professor. Sock it to me!');
INSERT INTO replik VALUES(21837,6,NULL,'Farnsworth',' Top secret news, everyone! I''ve developed a tiny one-man stealth fighter that''s virtually undetectable.');
