INSERT INTO replik VALUES(22027,6,NULL,'V-GINY',' Commence intercourse.');
INSERT INTO replik VALUES(22028,6,NULL,'Zapp',' The giant guns are making me feel sort of...inadequate.  I''m not sure if I couldâ');
